package com.ciardullo.conjugator.dao;import android.content.Context;import com.ciardullo.conjugator.vo.CommonVO;import com.ciardullo.conjugator.vo.Tense;/** * Performs DML on the tense table. */public class LoadTensesDAO extends LoadLookupTableDAO {	private static final String GET_TENSES = "tense";	private String[] selectionArgs;	@Override	protected CommonVO createVO(int theId, String theName) {		return new Tense(theId, theName);	}	public LoadTensesDAO(Context context) {		super(context);		setSqlStmt(GET_TENSES);	}	@Override	public String[] getSelectionArgs() {		return selectionArgs;	}	@Override	public void setSelectionArgs(String[] args) {		selectionArgs = args;	}	public String getOrderBy() {		return COLUMN_ID;	}}